        /// <summary>
        /// Configures the HttpClients.
        /// </summary>
        /// <param name="client"></param>
        /// <param name="builder"></param>
        /// <returns></returns>
        public HostConfiguration AddApiHttpClients
        (
            Action<System.Net.Http.HttpClient> client = null, Action<IHttpClientBuilder> builder = null)
        {
            if (client == null)
                client = c => c.BaseAddress = new Uri(ClientUtils.BASE_ADDRESS);

            List<IHttpClientBuilder> builders = new List<IHttpClientBuilder>();

            builders.Add(_services.AddHttpClient<IDonationsService, DonationsService>(client));
            builders.Add(_services.AddHttpClient<IModificationsService, ModificationsService>(client));
            builders.Add(_services.AddHttpClient<IOrdersService, OrdersService>(client));
            builders.Add(_services.AddHttpClient<IPaymentLinksService, PaymentLinksService>(client));
            builders.Add(_services.AddHttpClient<IPaymentsService, PaymentsService>(client));
            builders.Add(_services.AddHttpClient<IRecurringService, RecurringService>(client));
            builders.Add(_services.AddHttpClient<IUtilityService, UtilityService>(client));
            
            if (builder != null)
                foreach (IHttpClientBuilder instance in builders)
                    builder(instance);

            HttpClientsAdded = true;

            return this;
        }

