            writer.WriteString("number", card.Number);

            if (card._AuthenticationOption.IsSet)
            {
                writer.WritePropertyName("authentication");
                JsonSerializer.Serialize(writer, card.Authentication, jsonSerializerOptions);
            }
            if (card._BinOption.IsSet)
                writer.WriteString("bin", card.Bin);

            if (card._ConfigurationOption.IsSet)
            {
                writer.WritePropertyName("configuration");
                JsonSerializer.Serialize(writer, card.Configuration, jsonSerializerOptions);
            }
            if (card._CvcOption.IsSet)
                writer.WriteString("cvc", card.Cvc);

            if (card._DeliveryContactOption.IsSet)
            {
                writer.WritePropertyName("deliveryContact");
                JsonSerializer.Serialize(writer, card.DeliveryContact, jsonSerializerOptions);
            }
            if (card._ExpirationOption.IsSet)
            {
                writer.WritePropertyName("expiration");
                JsonSerializer.Serialize(writer, card.Expiration, jsonSerializerOptions);
            }
            if (card._LastFourOption.IsSet)
                writer.WriteString("lastFour", card.LastFour);

            if (card._ThreeDSecureOption.IsSet)
                writer.WriteString("threeDSecure", card.ThreeDSecure);

            if (card._UsageOption.IsSet)
                writer.WriteString("usage", card.Usage);
        }
    }
