            if (businessLineInfo.Service != null) 
            {
                string? serviceRawValue = BusinessLineInfo.ServiceEnum.ToJsonValue(businessLineInfo.Service);
                writer.WriteString("service", serviceRawValue);
            }
            
            if (businessLineInfo._CapabilityOption.IsSet && businessLineInfo.Capability != null) 
            {
                string? capabilityRawValue = BusinessLineInfo.CapabilityEnum.ToJsonValue(businessLineInfo._CapabilityOption.Value!.Value);
                writer.WriteString("capability", capabilityRawValue);
            }
            
            if (businessLineInfo._SalesChannelsOption.IsSet)
            {
                writer.WritePropertyName("salesChannels");
                JsonSerializer.Serialize(writer, businessLineInfo.SalesChannels, jsonSerializerOptions);
            }
            if (businessLineInfo._SourceOfFundsOption.IsSet)
            {
                writer.WritePropertyName("sourceOfFunds");
                JsonSerializer.Serialize(writer, businessLineInfo.SourceOfFunds, jsonSerializerOptions);
            }
            if (businessLineInfo._WebDataOption.IsSet)
            {
                writer.WritePropertyName("webData");
                JsonSerializer.Serialize(writer, businessLineInfo.WebData, jsonSerializerOptions);
            }
            if (businessLineInfo._WebDataExemptionOption.IsSet)
            {
                writer.WritePropertyName("webDataExemption");
                JsonSerializer.Serialize(writer, businessLineInfo.WebDataExemption, jsonSerializerOptions);
            }
        }
    }
