                string? verificationStatusRawValue = PayoutSettings.VerificationStatusEnum.ToJsonValue(payoutSettings._VerificationStatusOption.Value!.Value);
                writer.WriteString("verificationStatus", verificationStatusRawValue);
            }
        }
    }
}
