                if (additionalDataRiskStandalone.ThreeDAuthenticated != null)
                    writer.WriteString("threeDAuthenticated", additionalDataRiskStandalone.ThreeDAuthenticated);

            if (additionalDataRiskStandalone._ThreeDOfferedOption.IsSet)
                if (additionalDataRiskStandalone.ThreeDOffered != null)
                    writer.WriteString("threeDOffered", additionalDataRiskStandalone.ThreeDOffered);

            if (additionalDataRiskStandalone._TokenDataTypeOption.IsSet)
                if (additionalDataRiskStandalone.TokenDataType != null)
                    writer.WriteString("tokenDataType", additionalDataRiskStandalone.TokenDataType);
        }
    }
