            writer.WriteString("balancePlatform", transaction.BalancePlatform);

            writer.WriteString("bookingDate", transaction.BookingDate.ToString(BookingDateFormat));

            writer.WriteString("id", transaction.Id);

            if (transaction.Status != null) 
            {
                string? statusRawValue = Transaction.StatusEnum.ToJsonValue(transaction.Status);
                writer.WriteString("status", statusRawValue);
            }
            
            writer.WriteString("valueDate", transaction.ValueDate.ToString(ValueDateFormat));

            if (transaction._CreationDateOption.IsSet)
                writer.WriteString("creationDate", transaction._CreationDateOption.Value!.Value.ToString(CreationDateFormat));

            if (transaction._DescriptionOption.IsSet)
                writer.WriteString("description", transaction.Description);

            if (transaction._PaymentInstrumentOption.IsSet)
            {
                writer.WritePropertyName("paymentInstrument");
                JsonSerializer.Serialize(writer, transaction.PaymentInstrument, jsonSerializerOptions);
            }
            if (transaction._ReferenceForBeneficiaryOption.IsSet)
                writer.WriteString("referenceForBeneficiary", transaction.ReferenceForBeneficiary);

            if (transaction._TransferOption.IsSet)
            {
                writer.WritePropertyName("transfer");
                JsonSerializer.Serialize(writer, transaction.Transfer, jsonSerializerOptions);
            }
        }
    }
}
